import React from 'react';
import axios from 'axios';


class App extends React.Component{

  state ={
    title: '',
    body: '',
  };

  handleChange = ({target}) =>{
    const { name, value } = target;
    this.setState({[name]:value});
  };

  submit = (event) =>{
    event.preventDefault();

    const payload = {
      title: this.state.title,
      body: this.state.body,
    };

    axios({
      url:'http://localhost:3000/api/save',
      method: 'POST',
      data: payload
    })
    .then(() =>{
      console.log('Data has been sent');
      this.resetInputs();
    })
    .catch(() =>{
      console.log('Internal error');

    })
  }

  resetInputs = () =>{
    this.setState({
      title: '',
      body:'',
    });
  };

  render(){
    console.log('State ', this.state);
    //JSX
    return(
      <div>
        <h2>Hello World</h2>
        <form action="" onSubmit={this.submit}>
          <div className="form-input">
            <input type="text" name="title" placeholder="type name" value={this.state.value} onChange={this.handleChange} />            
          </div>
          <div className="form-input">
            <textarea name="body" placeholder='say something' cols="30" rows="10" value={this.state.body} onChange={this.handleChange}></textarea> 
          </div>

          <button>Submit</button>
        </form>
      </div>
    );   
  }

}

export default App;
