import React from 'react';
import axios from 'axios';


class App extends React.Component{

  state ={
    title: '',
    body: '',
  };

  handleChange = ({target}) =>{
    const { name, value } = target;
    this.setState({[name]:value});
  };

  submit = (event) =>{
    event.preventDefault();

    const payload = {
      title: this.state.title,
      body: this.state.body,
    };

    axios({
      url:'http://localhost:3000/lead/chci-nabidku',
      method: 'POST',
      data: payload
    })
    .then(() =>{
      console.log('Data has been sent');
      this.resetInputs();
    })
    .catch(() =>{
      console.log('Internal error');
    });
  }

  resetInputs = () =>{
    this.setState({title:'',body:''});
  };

  render(){
    console.log('State ', this.state);
    //JSX
    return(
      <div className='main-container'>

        <h2>Zadejte typ nemovitosti‍</h2>

        <form action="" onSubmit={this.submit}>

          <div className="form-input">
            <label htmlFor="">Typ nemovitosti‍</label>
              <select name="nemovitost" value={this.state.title} onChange={this.handleChange}>
                <option value="byt">Byt</option>
                <option value="dům">Dům</option>
                <option value="pozemek">Pozemek</option>
              </select>
              {/* <input type="text" name="nemovitost" placeholder="type name" value={this.state.value} onChange={this.handleChange} /> */}

          </div>

          <div className="form-input">

          </div>

          <button>Pokračovat</button>
        </form>

      </div>
    );   
  }

}

export default App;
