import React from 'react';
import axios from 'axios';

import Kraj from './components/kraj';
import Nemovitost from './components/nemovitost';




class App extends React.Component{

  state ={
    nemovitost: '',
  };

  handleChange = ({target}) =>{
    const { name, value } = target;
    this.setState({[name]:value});
  };

  submit = (event) =>{
    event.preventDefault();

    const payload = {
      nemovitost: this.state.nemovitost,
    };

    axios({
      url:'http://localhost:3000/lead/chci-nabidku',
      method: 'POST',
      data: payload
    })
    .then(() =>{
      console.log('Data has been sent');
      this.resetInputs();
    })
    .catch(() =>{
      console.log('Internal error');
    });
  }

  resetInputs = () =>{
    this.setState({nemovitost:''});
  };

  render(){
    console.log('State ', this.state);
    //JSX
    return(
      <div className='main-container'>

        <h2>Zadejte typ nemovitosti‍</h2>

        <form action="" onSubmit={this.submit}>
          <Nemovitost />
          {/* <Kraj /> */}
          <button>Pokračovat</button>
        </form>

      </div>
    );   
  }

}

export default App;
