import React from 'react';

class App extends React.Component{

  state ={
    title: '',
    body: '',
  };

  render(){

    //JSX
    return(
      <div>
        <h2>Hello World</h2>
        <form action="">
          <div className="form-input">
            <input type="text" name="title" placeholder="type name" value={this.state.value} onChange={2} />            
          </div>
          <div className="form-input">
            <textarea name="body" placeholder='say something' cols="30" rows="10" value={this.state.body} onChange={2}></textarea> 
          </div>

          <button>Submit</button>
        </form>
      </div>
    );   
  }

}

export default App;
