import React from "react";


class Nemovitost extends React.Component {

    state ={
        nemovitost: '',
      };
    
      handleChange = ({target}) =>{
        const { name, value } = target;
        this.setState({[name]:value});
      };
    
      submit = (event) =>{
        event.preventDefault();
    
        const payload = {
          nemovitost: this.state.nemovitost,
        };
    
        axios({
          url:'http://localhost:3000/lead/chci-nabidku',
          method: 'POST',
          data: payload
        })
        .then(() =>{
          console.log('Data has been sent');
          this.resetInputs();
        })
        .catch(() =>{
          console.log('Internal error');
        });
      }
    
      resetInputs = () =>{
        this.setState({nemovitost:''});
      };

    
    render(){
        return (
          <div className="form-input">
            <label htmlFor="">Typ nemovitosti‍</label>
              <select name="nemovitost" value={this.state.nemovitost} onChange={this.handleChange}>
                <option value="byt">Byt</option>
                <option value="dům">Dům</option>
                <option value="pozemek">Pozemek</option>
              </select>
          </div>
        );
    }
}

export default Nemovitost;
