    render(){
        return (
          <div className="form-input">
            <label htmlFor="">Typ nemovitosti‍</label>
              <select name="nemovitost" value={this.state.nemovitost} onChange={this.handleChange}>
                <option value="byt">Byt</option>
                <option value="dům">Dům</option>
                <option value="pozemek">Pozemek</option>
              </select>
          </div>
        );
    }
}

